package com.nextlabs.bae.helper;import java.io.FileInputStream;import java.io.IOException;import java.io.InputStream;import java.util.Properties;import org.apache.commons.logging.Log;import org.apache.commons.logging.LogFactory;public class PropertyLoader {	public static String BAEADFrontEndPropertiesPath;	public static String BAESToolConstantPath;	public static Properties bAESProperties;	public static Properties bAESConstant;	private static final Log LOG = LogFactory.getLog(PropertyLoader.class);	/**	 * Load properties file for application	 * 	 * @exception IOException	 *                Any IO exception	 */	public static void loadProperties() {		bAESProperties = new Properties();		try {			LOG.info("PropertyLoader loadProperties(): Loading properties file at "					+ BAEADFrontEndPropertiesPath);			InputStream in = new FileInputStream(BAEADFrontEndPropertiesPath);			bAESProperties.load(in);			in.close();		} catch (IOException e) {			LOG.error("PropertyLoader loadProperties(): " + e.getMessage(), e);		}	}	/**	 * Load constant file for application	 * 	 * @exception IOException	 *                Any IO exception	 */	public static void loadConstant() {		bAESConstant = new Properties();		try {			LOG.info("PropertyLoader loadConstant(): Loading constant file at "					+ BAESToolConstantPath);			InputStream in = new FileInputStream(BAESToolConstantPath);			bAESConstant.load(in);			in.close();		} catch (IOException e) {			LOG.error("PropertyLoader loadProperties(): " + e.getMessage(), e);		}	}}